import React, { useState, useEffect } from 'react';
import { ethers } from "ethers";
import styled from 'styled-components';

export default function PolicyInputPanel() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const [policyName, setPolicyName] = useState('');
    const [policyContent, setPolicyContent] = useState('');
    const [policyPremium, setPolicyPremium] = useState('');
    const [policyDuration, setPolicyDuration] = useState('');

    return (
        <div className="ui container">
            <div className="ui form">
                <div className="field">
                    <label>Policy Name</label>
                    <input type="text" placeholder="Policy Name" onChange={e => {console.log(e.target.value)}}/>
                </div>
                <div className="field">
                    <label>Policy Content</label>
                    <input type="text" name="policy-content" placeholder="Policy Content" />
                </div>
                <div className="field">
                    <label>Policy Premium</label>
                    <input type="number" name="policy-premium" placeholder="Policy Premium" />
                </div>
                <div className="field">
                    <label>Policy Duration</label>
                    <input type="date" name="policy-duration" placeholder="Policy Duration" />
                </div>
                <button class="ui button right floated" type="submit">Submit</button>
            </div>
        </div>
    )
}
