import React, { useState, useEffect } from 'react';
import { ethers } from "ethers";
import { Contract } from "@ethersproject/contracts";
import styled from 'styled-components';
import { RINKEBY_ID, addresses, abis } from "@uniswap-v2-app/contracts";
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import Avatar from '@material-ui/core/Avatar';
import ipfs from '../../ipfs';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import FacebookLogin from 'react-facebook-login';
import PolicyIcon from '@material-ui/icons/Policy';
import CssBaseline from '@material-ui/core/CssBaseline';
import AccountCircle from '@material-ui/icons/AccountCircle';
import FacebookIcon from '@material-ui/icons/Facebook';
import InputAdornment from '@material-ui/core/InputAdornment';
import { DateTimePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import DateFnsUtils from '@date-io/date-fns'; // choose your lib


const useStyles = makeStyles((theme) => ({
    container: {
      marginTop: "20px",
      width: '800px',
      marginLeft: 'auto',
      marginRight: 'auto',
      backgroundColor: 'white',
      padding: '30px 30px 30px 30px',
      borderRadius: '10px'
    },
    avatar: {
      margin: theme.spacing(1),
      backgroundColor: theme.palette.secondary.main,
    },
    paper: {
      marginTop: theme.spacing(8),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    textField: {
      marginLeft: theme.spacing(1),
      marginRight: theme.spacing(1),
      width: 200,
    },
    form: {
      width: '100%', // Fix IE 11 issue.
      marginTop: theme.spacing(3),
    },
}));


export default function PolicyInputPanel() {
    const classes = useStyles()

    const [loading, setLoading] = useState(false);
    const [policyName, setPolicyName] = useState(0);
    const [policyType, setPolicyType] = useState(0);
    const [policyContent, setPolicyContent] = useState('');
    const [policyPremium, setPolicyPremium] = useState(0);
    const [policyDuration, setPolicyDuration] = useState(new Date());
    const [policyDeadline, setPolicyDeadline] = useState(new Date());

    const [buffer, setBuffer] = useState('');
    const [contentHash, setContentHash] = useState('');
    const [login, setLogin] = useState(false);
    const [data, setData] = useState({});

    const responseFacebook = (response) => {
      console.log(response);
      setData(response);
      if (response.accessToken) {
        setLogin(true);
      } else {
        setLogin(false);
      }
    }

    // useEffect(() => {
    //   const getCurrentTime = () => {
    //     const today = new Date().toISOString().split(".")[0];
    //     setNow(today.toString());
    //   }
    //   getCurrentTime();
    // }, [])

    // // after today
    // const today = new Date().toISOString().split("T")[0];
    // function dateToUnix(value) {
    //     const date = new Date(value);
    //     return date.getTime();
    // }

    const captureFile = async event => {
      console.log('capture file...');
      event.preventDefault();
      const file = event.target.files[0];
      const reader = new window.FileReader();
      reader.readAsArrayBuffer(file);
      reader.onloadend = () => {
        setBuffer(Buffer.from(reader.result));
      }
      // upload
      let data = buffer;
      console.log('Submit this: ', data);
      const postResponse = await ipfs.add(data);
      console.log("hash: ", postResponse['path']);
      setPolicyContent(postResponse['path']);
    }


    async function createPolicy() {
        try {
            let tx;
            setLoading(true);
            const provider = new ethers.providers.Web3Provider(window.ethereum);
            const signer = provider.getSigner()
            const policyFactoryContract = new Contract(addresses[RINKEBY_ID].policyFactory, abis.policyFactory, signer);
            console.log(policyFactoryContract)
            tx = await policyFactoryContract.createPolicy(
                policyName,
                policyType,
                policyContent,
                policyPremium,
                policyDuration
            );
          // until transactionHash is mined.
            await provider.waitForTransaction(tx.hash);
            setLoading(false);
            console.log(tx);
            } catch (err) {
            console.log(err)
            }
            setLoading(false);
        }
    return (
      <div>
      <Grid container justify = "center">
        <FacebookLogin
        appId="360104115420180"
        autoLoad={false}
        fields="name,email"
        scope="public_profile"
        callback={responseFacebook}
        icon="fa-facebook" />
      </Grid>

      <MuiPickersUtilsProvider utils={DateFnsUtils}>
      <Container component="main" maxWidth="md">
        <CssBaseline />
        <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <PolicyIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Create Policy
        </Typography>
      <form className={classes.form} noValidate>
      <Grid container spacing={4}>
      <Grid item xs={12} sm={6}>
          <TextField
            required
            id="Proposer"
            label="Proposer"
            value={data.name}
            disabled
            fullWidth
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <AccountCircle />
                </InputAdornment>
              ),
            }}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="UserId"
            label="UserId"
            value={data.id}
            disabled
            fullWidth
            InputProps={{
              startAdornment: (
              <InputAdornment position="start">
                <FacebookIcon />
              </InputAdornment>
              ),
            }}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="policyName"
            label="Title"
            onChange={e => setPolicyName(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={6}>
        <TextField
          id="standard-select-type"
          required
          select
          label="Type"
          value={policyType}
          onChange={e => setPolicyType(e.target.value)}
          helperText="Please select your insurance type"
          fullWidth
        >
            <MenuItem value="Cyper Insurance">Cyper Insurance</MenuItem>
            <MenuItem value="Health Insurance">Health Insurance</MenuItem>
            <MenuItem value="Investment Insurance">Investment Insurance</MenuItem>
            <MenuItem value="Auto Insurance">Auto Insurance</MenuItem>
            </TextField>
        </Grid>

        <Grid item xs={12} sm={6}>
        <DateTimePicker
            value={policyDeadline}
            disablePast
            onChange={e => console.log(e.target.value)}
            label="Deadline"
            showTodayButton
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={6}>
        <DateTimePicker
            value={policyDuration}
            disablePast
            onChange={setPolicyDuration}
            label="Duration"
            showTodayButton
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={9}>
          <TextField
            required
            id="policyContent"
            name="policyContent"
            label="Content Hash"
            value={policyContent}
            disabled
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={3}>
        <input
                type="file"
                accept=".pdf,.word"
                className={classes.input}
                style={{ display: 'none', }}
                id="contained-button-file"
                multiple
                onChange={e => captureFile(e)}
              />
            <label htmlFor="contained-button-file">
            <Button variant="contained" component="span" className={classes.button} startIcon={<CloudUploadIcon />}>
              Upload Policy
            </Button>
            </label> 
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="Policypremium"
            name="Policypremium"
            label="Claim Base"
            onChange={e => setPolicyPremium(e.target.value)}
            fullWidth
          />
        </Grid>

        <Grid justify="flex-end" container>
          <Grid>
