// import styled from "styled-components";
import React from 'react';
// import { ReactComponent as LogoIcon } from '../../assets/logo.svg'


const getNetwork = async (provider) => {
    if (provider) {
        const network = await provider.getNetwork()
        return (network.name)
    } else {
        return ('No network detected')
    }

}

async function WalletButton({ provider, loadWeb3Modal, logoutOfWeb3Modal }) {
    console.log(await getNetwork(provider))
    return (
        <div>
            {/* <div className="ui button">
                {getNetwork(provider)}
            </div> */}
            <div className="ui button"
                onClick={() => {
                    if (!provider) {
                        loadWeb3Modal();
                    } else {
                        logoutOfWeb3Modal();
                    }
                }}
            >
                {!provider ? "Connect Wallet" : "Disconnect Wallet"}
            </div>
        </div>

    );
}

export default function Header({ provider, loadWeb3Modal, logoutOfWeb3Modal }) {
    return (
        <div className="ui secondary pointing menu">
            <a className="item" href="/">
                Swap
            </a>
            <a className="item" href="/">
                Funding Pool
            </a>
            <div className="right menu">
                <div className="item">
                    <WalletButton provider={provider} loadWeb3Modal={loadWeb3Modal} logoutOfWeb3Modal={logoutOfWeb3Modal} />
                </div>
            </div>
        </div>

    );
}
