import Jumbotron from '../Jumbotron';
import CurrencyInputPanel from '../CurrencyInputPanel';
import { ethers } from "ethers";



export default function PoolPage() {



    return (
        <div>
            <Jumbotron title="Provide Liquidity" description="Liquidity providers earn a 0.3% fee on all trades proportional to their share of the pool. Fees are added to the pool, accrue in real time and can be claimed by withdrawing your liquidity." />
            <CurrencyInputPanel />
        </div>
    );
}
