import React, { useState, useEffect } from 'react';
import { ethers } from "ethers";
import { Contract } from "@ethersproject/contracts";
import styled from 'styled-components';
import { RINKEBY_ID, addresses, abis } from "@uniswap-v2-app/contracts";
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import ipfs from '../../ipfs';


const useStyles = makeStyles((theme) => ({
    container: {
      marginTop: "20px",
      width: '800px',
      marginLeft: 'auto',
      marginRight: 'auto',
      backgroundColor: 'white',
      padding: '30px 30px 30px 30px',
      borderRadius: '10px'
    },
}));


export default function PolicyInputPanel() {
    const classes = useStyles()

    const [loading, setLoading] = useState(false);
    const [policyName, setPolicyName] = useState(0);
    const [policyType, setpolicyType] = useState(0);
    const [policyContent, setPolicyContent] = useState(0);
    const [policyPremium, setPolicyPremium] = useState(0);
    const [policyDuration, setPolicyDuration] = useState(0);
    const [buffer, setBuffer] = useState('');

    // after today
    const today = new Date().toISOString().split("T")[0];

    function dateToUnix(value) {
        const date = new Date(value);
        return date.getTime();
    }

    const captureFile = (event) => {
      console.log('capture file...');
      event.preventDefault();
      const file = event.target.files[0];
      const reader = new window.FileReader();
      reader.readAsArrayBuffer(file);
      reader.onloadend = () => {
        setBuffer(Buffer(reader.result));
        console.log('buffer', buffer);
      }
    }

    const upload = (event) => {
      event.preventDefault();
      ipfs.files.add(buffer, (error, result) => {
        if (error) {
          console.error(error);
          return
        }
        console.log('ipfsHash:', result[0].hash)
      })
    }


    async function createPolicy() {
        try {
            let tx;
            setLoading(true);
            const provider = new ethers.providers.Web3Provider(window.ethereum);
            const signer = provider.getSigner()
            const policyFactoryContract = new Contract(addresses[RINKEBY_ID].policyFactory, abis.policyFactory, signer);
            console.log(policyFactoryContract)
            tx = await policyFactoryContract.createPolicy(
                policyName,
                policyType,
                policyContent,
                policyPremium,
                policyDuration
            );
          // until transactionHash is mined.
            await provider.waitForTransaction(tx.hash);
            setLoading(false);
            console.log(tx);
            } catch (err) {
            console.log(err)
            }
            setLoading(false);
        }

    return (
     <Grid container className={classes.container} gutterBottom>
     <React.Fragment>
      <Typography variant="h6" gutterBottom>
        Create Policy
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="policyName"
            name="policyName"
            label="policyName"
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={6}>
            <InputLabel id="policyType">policyType</InputLabel>
            <Select
            labelId="policyType"
            id="policyType"
            value={policyType}
            onChange={e => setpolicyType(e.target.value)}
            fullWidth
            >
            <MenuItem value="">
                <em>None</em>
            </MenuItem>
            <MenuItem value="Cyper Insurance">Cyper Insurance</MenuItem>
            <MenuItem value="Health Insurance">Health Insurance</MenuItem>
            <MenuItem value="Investment Insurance">Investment Insurance</MenuItem>
            <MenuItem value="Auto Insurance">Auto Insurance</MenuItem>
            </Select>
        </Grid>
        <Grid item xs={12}>
          <TextField
            required
            id="policyContent"
            name="policyContent"
            label="policyContent"
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="Policypremium"
            name="Policypremium"
            label="Policypremium"
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField id="state" name="state" label="State/Province/Region" fullWidth />
        </Grid>
        <Grid>
        <input
          type="file"
          accept=".pdf,.word"
          className={classes.input}
          // style={{ display: 'none', }}
          id="raised-button-file"
          multiple
          onChange={e => captureFile(e)}
      />
      <label htmlFor="raised-button-file">
        <Button component="span" className={classes.button} onClick={upload}>
          Upload content
        </Button>
        </label> 
      </Grid>
      </Grid>
    </React.Fragment>
        </Grid>
    )
}



