        //         return;
        //       }
        //     provider.on("chainChanged", async (chainId) => {
        //         console.log(chainId)
        //   });
        // }

        // subscribeProvider();
        getNetwork();
        console.log(provider)
    }, [provider])




    return (
        <WalletButton provider={provider} loadWeb3Modal={loadWeb3Modal} logoutOfWeb3Modal={logoutOfWeb3Modal} />

    );
}
