import React from "react";
import Jumbotron from '../Jumbotron';
import styled from 'styled-components';

const Form = styled.form`
    width: 500px;
    height: 250px;
    border-left: 10px solid #000;
    border-right: 10px solid #FFF;
    padding: 10px 0;
    margin: 50px auto;
    background-image: -webkit-linear-gradient(left, #000 50%, #FFF 50%), -webkit-linear-gradient(left, #000 50%, #FFF 50%);
    background-image: -moz-linear-gradient(left, #000 50%, #FFF 50%), -moz-linear-gradient(left, #000 50%, #FFF 50%);
    background-image: -o-linear-gradient(left, #000 50%, #FFF 50%), -o-linear-gradient(left, #000 50%, #FFF 50%);
    background-image: linear-gradient(to right, #000 50%, #FFF 50%), linear-gradient(to right, #000 50%, #FFF 50%);
    background-position: 0 0, 0 100% ;
    background-repeat: no-repeat;
    background-size: 100% 10px;
    `

export default function PolicyPage() {
    return (
        <div>
            <Jumbotron 
            title="Policies" 
            description="Propose your own policies." />
            <div className="ui container">

                <div className="ui form">
                    <div className="field">
                        <label>Policy Name</label>
                        <input type="text" name="policy-name" placeholder="Policy Name" />
                    </div>
                    <div className="field">
                        <label>Policy Content</label>
                        <input type="text" name="policy-content" placeholder="Policy Content" />
                    </div>
                    <div className="field">
                        <label>Policy Premium</label>
                        <input type="number" name="policy-premium" placeholder="Policy Premium" />
                    </div>
                    <div className="field">
                        <label>Policy Duration</label>
                        <input type="date" name="policy-duration" placeholder="Policy Duration" />
                    </div>
                    <button class="ui button right floated" type="submit">Submit</button>
                </div>
            </div>
        </div>
    );
}
