import React, { useState, useEffect } from 'react';
import { RINKEBY_ID, addresses, abis } from "@uniswap-v2-app/contracts";
import { Contract } from "@ethersproject/contracts";
import { ethers } from "ethers";
import useWeb3Modal from "../../hooks/useWeb3Modal";
import { ChainId, Token, WETH, Fetcher, Route } from '@uniswap/sdk'

const projectId = "66fbccb2856b40b3a622d925568379e9";
const projectSecret = "275ed56f36e440e0ab7cad94a3310aae";

async function getPairAmount(provider, tokenAmount) {
  if (!provider) {
    return
  }
  const SycWethExchangeContract = new Contract(addresses[RINKEBY_ID].pairs["SYC-WETH"], abis.pair, provider);
  const reserves = await SycWethExchangeContract.getReserves();
  // reserves[0] is SYC, reserves[1] is ETHER
  const k = reserves[0].mul(reserves[1])
  const y = ethers.utils.parseUnits(tokenAmount,"ether").add(reserves[1])
  const x = ethers.utils.formatEther(reserves[0].sub(k.div(y)))

  return x.toString();
}


export default function CurrencyInputPanel ({provider}) {
  const [ETHAmount, setETHAmount] = useState(0);
  const [SYCAmount, setSYCAmount] = useState(0);
  useEffect(() => {
    const fetchPrice = async () => {
      if (ETHAmount && typeof ETHAmount == 'number') {
        setSYCAmount(await getPairAmount(provider, ETHAmount))
      }
    }
    fetchPrice();
    setSYCAmount(ETHAmount);
  }, [ETHAmount]);


    return (
      <div>
        <div className="ui container" style={{ marginTop: "20px" }}>
          <div className="ui big form">
            <div className="ui grid">
              <div className="field six wide column">
                <label>Amount</label>
                <input placeholder="ETH" type="text" onChange={e => setETHAmount(e.target.value) } />
              </div>
              <div className="field four wide column center aligned">
              <i className="fas fa-exchange-alt fa-2x"></i>
              </div>
              <div className="field six wide column">
                <label>Amount to be received</label>
                <input placeholder="SYC" type="text" disabled="disabled" value={SYCAmount}/>
              </div>
            </div>
            <div className="ui submit button right floated">Swap</div>
          </div>
        </div>
      </div>
    );
  }
